import { useState } from 'react';


const Task = () => {
    const [tasks, setTasks] = useState( [
            {
                id: '1',
                text: 'Doctor Appointment',
                day: 'Feb 5th at 2:30pm',
                reminder: true

            },
            {
                id: '2',
                text: 'Meeting at School',
                day: 'Feb 6th at 1:30pm',
                reminder: true

            },
            {
                id: '3',
                text: 'Food Shopping',
                day: 'Feb 5th at 2:30pm',
                reminder: false

            },

        ]
    )
    return (
        <>
            {tasks.map((task) => (<h3 key={task.id}>{task.text}</h3>))}
        </>
    )
}

export default Task
